import React, { useState , useEffect } from 'react'
import axios from 'axios'
import Loader from './Loader'

import '../../Assets/Styles/Job/Job.css'


//Full Jobs
function Jobs() {
  const url = `https://dummyjson.com/products/`
  const [products, setProducts] = useState({
    loading: false,
    data: null,
    error: false
  })



  useEffect(()=>{
    setProducts({
      loading:true,
      data: null,
      error: false

    })

    axios.get(url)
    .then(response =>{
        setProducts({
          loading:false,
          data: response.data,
          error: false
        })

        .catch(() =>{
          setProducts({
            loading:false,
            data: null,
            error: true
          })
        })
    })
  }, [url])

  let content = null

  if(products.loading){
    content = <Loader/>
  }

  if(products.error){
    content = <p>Xəta baş verdi, yenidən yoxlayın.</p>
  }

  if(products.data){
    content = 
    products.data.map((product) => 
        <div key={product.id} className="flex-no-shrink w-full md:w-1/4 md:px-3">

        </div>
    )
}


  return (
    <div>Jobs
      {content}


    </div>
  )
}

export default Jobs
