import React from 'react'
import { Link } from "react-router-dom";


function JobCard(props) {
    // const background = props.product.companyImage;
  return (
    <div className='border-2 mb-4 rounded overflow-hidden container'>
         <Link to={`/job/${props.product.id}`}>
         <div 
            style={{
                        'background': `')`, "height":'90px',
                    }}
                    className="w-100  myCard d-flex ">
                        <img src={props.product.companyImage}/>
                        <h3>{props.product.name}</h3>
        </div>
        </Link>

    </div>
  )
}

export default JobCard
