import React, { useState , useEffect } from 'react'
import axios from 'axios'
import { useParams } from 'react-router-dom'
import '../../Assets/Styles/Job/Job.css'

function Jobs() {
  const id = {useParams}
  const url = `https://dummyjson.com/products/${id}`
  const [job, setJob] = useState({
    loading:false,
    data: null,
    error: false,
  })

  let content = null

  useEffect(() =>{
    setJob({
      loading: true,
      data: null,
      error:false,
    })

    axios.get(url)
      .then(response=>{
        setJob({
          loading: true,
          data: response.data,
          error:false,
        })
      })
      .catch(()=>{
        setJob({
          loading: true,
          data: null,
          error:false,

        })
      })
    }, [url])

    if(job.error){
      content = <p>Xəta baş verdi, zəhmət olmasa səhifəni yeniləyin və ya daha sonra müraciət edin.</p>
    }

    if(job.data){
      content=<div>
        <h1>{job.data.title}</h1>
        <div><img src={job.data.images[0].imageUrl} alt={job.data.name}/></div>
        <div>$ {job.data.price}</div>
        <div>$ {job.data.description}</div>

      </div>
    }





  return (
    <div>{content}</div>
  )
}

export default Jobs
