import Loader from './Loader'


//Full Jobs
function Home() {
  const {id} = useParams()
  const url = `https://dummyjson.com/products/${id}`
  const [products, setProduct] = useState({
    loading: false,
    data: null,
    error: false
  })

  let content = null

  useEffect(()=>{
    setProduct({
      loading:true,
      data: null,
      error: false

    })

    axios.get(url)
    .then(response =>{
        setProduct({
          loading:false,
          data: response.data,
          error: false
        })

        .catch(() =>{
          setProduct({
            loading:false,
            data: null,
            error: true
          })
        })
    })
