import React from 'react'

function JobCard(props) {
    // const background = props.product.companyImage;
  return (
    <div className='border-2 mb-4 rounded overflow-hidden'>
         <Link to={`/jobs/${props.product.id}`}>
         <div 
            style={{
                        'backgroundImage': `url('${props.product.companyImage}')`,
                    }}
                    className="w-100 h-100 ">
                        {props.product.name}
        </div>
        </Link>

    </div>
  )
}

export default JobCard
