import React, { useState , useEffect } from 'react'
import axios from 'axios'
// import '../../Assets/Styles/Job/Job.css'
import { useParams } from 'react-router-dom'
import Loader from './Loader'
import '../Jobs/Home.css'


//Job Details
function Home() {
  const {id} = useParams()
  const url = `http://localhost:3000/users/${id}`
  const [product, setProduct] = useState({
    loading: false,
    data: null,
    error: false
  })

  let content = null

  useEffect(()=>{
    setProduct({
      loading:true,
      data: null,
      error: false

    })

    axios.get(url)
    .then(response =>{
        setProduct({
          loading:false,
          data: response.data,
          error: false
        })

        .catch(() =>{
          setProduct({
            loading:false,
            data: null,
            error: true
          })
        })
    })
  }, [url])

  if(product.loading){
    content = <Loader/>
  }

  if(product.error){
    content = <p>Xəta baş verdi, yenidən yoxlayın.</p>
  }

  if(product.data){
   content =
    <div className='card '>
      <div className='card-header'>
        <div className='d-flex'> 
        <div className='me-5'>
          <img src= {product.data.companyImage}/>
        </div>
          <h1>{product.data.name}</h1>
        </div>

      <div><p> {product.data.Skills}</p></div>
      </div>



      <div className='card-body'>
        <div className='top_infos d-flex'>
          <p className='me-2'>{product.data.companyName}</p>
          <p>{product.data.City}</p>

        </div>

        <div className='body_infos'>
          <div>
          <h3>Əsas Vəzifə Öhdəlikləri</h3>
          <p>{product.data.MainWork}</p>
          </div>
        </div>

      </div>
    <div  className='card-footer'>{product.data.DateTime}</div>


    </div>

  }

    return (
      <div className='container'>

        <div className='d-flex justify-content-center'>{content}</div>
    </div>


  )
}

export default Home
